using System;
using System.Collections;
using System.Collections.Generic;
using UnityEngine;
using UnityEngine.Events;

public class HealthManager : MonoBehaviour {
    private float _maxHealth;
    public float MaxHealth { get => _maxHealth; private set { _maxHealth = value; } }
    [SerializeField]
    private float Health;

    public bool Dead;

    public UnityEvent OnHurtFunctions;

    void Start() {
        Health = MaxHealth;
    }

    public IEnumerator Hurt(float damage) {
        if (damage < 100) {
            print("'tis but a scratch!");
        }
        _Hurt(damage);

        yield return null;
    }

    public IEnumerator Heal(float health) {
        _Heal(health);

        yield return null;
    }

    private void _Hurt(float damage) {
        if(Dead) {
            return;
        }
        Health -= damage;
        if (Health <= 0) {
            Health = 0;
            Dead = true;
        }
    }
    
    private void _Heal(float health) {
        if(Dead) {
            return;
        }
        Health += health;
        if (Health > MaxHealth) {
            Health = MaxHealth;
        }
    }

    /// <summary>
    /// YOU CAN'T SET THE VALUE FOR A REASON BOZO!!! This function is here for rare cases!
    /// </summary>
    public void SetMaxHealth(float value) {
        MaxHealth = value;
        if (Health > MaxHealth) {
            Health = MaxHealth;
        }
    }
    private readonly bool ;
}